import { Link } from "react-router-dom";

const features = [
  {
    title: "Detect Crop Disease",
    description: "Identify crop diseases early and take preventive actions.",
    image: "data:image/jpeg;base64,", // Replace with actual image URL
    link: "/detect-disease",
  },
  {
    title: "Predict Crop Yield",
    description: "Estimate crop yield based on environmental and soil factors.",
    image: "data:image/jpeg;base64,",
    link: "/predict-yield",
  },
  {
    title: "Crop Recommendation",
    description: "Get best crop recommendations based on soil and climate data.",
    image: "data:image/jpeg;base64,",
    link: "/crop-recommendation",
  },
  {
    title: "Smart Irrigation",
    description: "Optimize water usage for better crop growth.",
    image: "data:image/jpeg;base64,",
    link: "/smart-irrigation",
  },
];

export default function HomePage() {
  return (
    <div className="min-h-screen bg-green-100 flex flex-col items-center">
      {/* Header */}
      <header className="w-full bg-green-600 text-white text-center py-6 text-3xl font-bold shadow-md">
        KrishiMitra
      </header>

      {/* Description */}
      <p className="text-gray-700 text-lg text-center mt-6 px-4 max-w-2xl">
        Empowering farmers with AI-powered solutions to enhance agricultural productivity.
      </p>

      {/* Feature Cards */}
      <div className="flex flex-nowrap justify-center gap-6 mt-10 p-4 overflow-x-auto max-w-full">
        {features.map((feature, index) => (
          <Link to={feature.link} key={index} className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-xl transition duration-300 w-64">
            <img src={feature.image} alt={feature.title} className="w-full h-40 object-cover rounded-lg" />
            <h2 className="text-xl font-semibold mt-4">{feature.title}</h2>
            <p className="text-gray-600 mt-2">{feature.description}</p>
          </Link>
        ))}
      </div>

      {/* Footer */}
      <footer className="w-full bg-green-600 text-white text-center py-4 mt-10">
       copy right &copy; {new Date().getFullYear()} KrishiMitra. All rights reserved.
      </footer>
    </div>
  );
}
